      #[derive(Copy, Clone)]
      #[allow(dead_code)]
      enum Color {
          Red = 0,
          Green = 1,
          Blue = 2,
      }

      union IntOrColor {
          i: u8,
          c: Color,
      }

      fn main() {
          let u = IntOrColor { i: 42 };

          // Undefined behavior reading an invalid discriminant from the 'Color' enumeration type
          unsafe { u.c };  // Noncompliant
      }

